import React from 'react';
import "./PokemonInfo.css"

const pokemons = require("../../constants/data.json");

const PokemonInfo = ({ pokemon = pokemons[pokemon].id }) => (
    <div className='app__pokemoninfo-pokemon'>
        <h1 className='headtext'>{pokemon.name}</h1>
        <img src={pokemon.img} alt="pokemon" />
        <div className='app__pokemoninfo-pokemon_type'>
            <h2 className='subtitle'>Type:</h2>
            {pokemon.type.map((type) => {
                return (
                    <button className="type__button">{type}</button>
                )
            })}
        </div>
        <div className='app__pokemoninfo-pokemon_weakness'>
            <h2 className='subtitle'>Weakness:</h2>
        </div>
    </div >
)

export default PokemonInfo;
