import React, { useState } from 'react'
import { PokemonInfo, SelectBar } from "../../components"
import "./Pokemon.css"

const pokemons = require("../../constants/data.json");


const Pokemon = () => {

    const getInitialPokemon = () => {
        const pokemon = pokemons[0];
        return pokemon;
    };

    const [currentPkmn, setPokemon] = useState(getInitialPokemon);

    const handleChange = (e) => {
        setPokemon(e.target.pokemon)
    }

    return (
        <div className='section__padding'>
            <SelectBar onChange={() => setPokemon(currentPkmn)} />
            <PokemonInfo currentPkmn={currentPkmn} />
        </div>
    )
}

export default Pokemon;
