import React, { useState } from 'react'
import { PokemonInfo, SelectBar } from "../../components"
import "./Pokemon.css"

const pokemons = require("../../constants/data.json");

const Pokemon = () => {

    const [currentPkmn, setPokemon] = useState(0);

    return (
        <div className='section__padding'>
            <SelectBar currentPkmn={currentPkmn} onChange={() => setPokemon(currentPkmn)} />
            <PokemonInfo currentPkmn={currentPkmn} />
        </div>
    )
}

export default Pokemon;
